import { useState } from "react"
import { Link } from "react-router-dom"

function Login(props){

    const [eusername,seteusername] = useState()
    const [epassword,setepassowrd] = useState()
    const [ruser,setruser] = useState()

    const users = props.users


        function handleUInput(evt){
            seteusername(evt.target.value)
        }

        function handlePInput(evt){
            setepassowrd(evt.target.value )
        }

        function checkUser(){
            users.forEach(function(item){
                if(item.username === eusername && item.password === epassword){
                    console.log("Login Successfully")
                }else{
                    console.log("Login failed")
                    setruser(false)
                }
            })
        }
   

    return(
        <div className="bg-black p-10">
        <div className="bg-[#EFEFEF] p-10 border rounded-md">
        <h1 className="text-3xl font-medium ">Hi Hello!</h1>
        {ruser?<p>I help you manage your activities after your login:)</p> : <p className="text-red-600___">"Please Sign Up Before Login"</p>}
        <div className="flex flex-col  my-1">               
        <input  type="text" className="w-52 border-black  p-1 bg-transparent border rounded-md"
        placeholder="username" onChange={handleUInput}  /><br />
        <input type="password" className="w-52 border-black p-1 bg-transparent border rounded-md"
        placeholder="enter your password" onChange={handlePInput} /><br />
 
        </div>
        <button className="bg-[#8272DA] w-24 p-1 rounded-md" onClick={checkUser}>Login</button>

        <p>Don't have a account? <Link to={"signup"} className="underline">Sign up</Link></p>
        
        </div>
    </div>
    )
}

export default Login
